import { Component, OnInit } from '@angular/core';
import { ProductsDataService } from 'src/app/services/products-data.service';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  constructor(private productsDataService: ProductsDataService){}
  product!: {id: number, img: string, cat: string, title: string, price: string, desc: string, colors: Array<string>, sizes: Array<string>}
  value: number = 1
  ngOnInit() {
    this.productsDataService.productsDataEmitter.subscribe((data: {id: number, img: string, cat: string, title: string, price: string, desc: string, colors: Array<string>, sizes: Array<string>}) => {
      this.product = data
    })
  }

  decreaseValue() {
    if(this.value>0) this.value--
  }

  increaseValue() {
    if(this.value<9) this.value++
  }
}
