import { Directive, ElementRef, Renderer2 } from '@angular/core';

@Directive({
  selector: '[appSlideIndex]'
})
export class SlideIndexDirective {
  constructor(private element: ElementRef, private renderer: Renderer2) { }
  slideIndex: number = this.element.nativeElement.getAttribute("appSlideIndex")
  ngOnInit(): void {
    console.log(this.sli)
    this.renderer.setStyle(this.element.nativeElement, "transform", (this.slideIndex * -100)+"vw")
  }
}
