import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductsDataService } from 'src/app/services/products-data.service';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  constructor(private productsDataService: ProductsDataService, private activatedRoute: ActivatedRoute){}
  product!: {id: number, img: string, cat: string, title: string, price: string, desc: string, colors: Array<string>, sizes: Array<string>} | undefined
  productId: any
  value: number = 1
  ngOnInit() {
    this.productId = this.activatedRoute.snapshot.paramMap.get('id')
    this.product = this.productsDataService.productsData.find(x => x.id == this.productId)
  }

  decreaseValue() {
    if(this.value>1) this.value--
  }

  increaseValue() {
    if(this.value<10) this.value++
  }

  addToCart() {

  }
}
