import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductsData } from 'src/app/models/products-data.model';
import { ProductsDataService } from 'src/app/services/products-data.service';
import { SearchDataService } from 'src/app/services/search-data.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit{
  constructor(private productsDataService: ProductsDataService, private searchDataService: SearchDataService, private activatedRoute: ActivatedRoute) {}
  productsData!: ProductsData[]
  searchText: string = ""
  category!: string | null
  currentRoute!: string | undefined
  data!: any
  parsedData!: any
  unique: boolean = true

  ngOnInit(): void {
    this.productsDataService.getProducts().subscribe(res => this.productsData = res)
    this.currentRoute = this.activatedRoute?.component?.name
    this.category = this.activatedRoute.snapshot.paramMap.get("cat")
    if(this.category ) this.productsData = this.productsData.filter(x => x.cat === this.category)
    else {
      this.productsData = this.productsData.filter(x => x.bestseller === true)
      this.productsData.splice(8, this.productsData.length- 8)
    }
    this.searchDataService.searchDataEmitter.subscribe((data:string) => this.searchText = data)
  }

  ngAfterViewInit(){

  }

  addToCart(product: any) {
    for (let i = 1; i <= localStorage.length; i++) {
      this.data = localStorage.getItem(i.toString())
      if (this.data != null) {
        this.parsedData = JSON.parse(this.data)
        console.log(this.parsedData.id, product.id);

        if (this.parsedData.id === product.id) {
          this.unique = false
          break
        }
        else this.unique = true
      }
    }

    if (this.unique) localStorage.setItem((localStorage.length).toString(), JSON.stringify({ id: product.id, img: product.img, title: product.title, price: product.price, color: "black", size: "S", quantity: 1 }))
  }

  addToWishlist(product: any) {
    for (let i = 0; i < sessionStorage.length; i++) {
      this.data = sessionStorage.getItem(i.toString())
      this.parsedData = JSON.parse(this.data)
      if (this.parsedData.id === product.id) {
        this.unique = false
        break
      }
      else this.unique = true
    }
    if (this.unique) sessionStorage.setItem((sessionStorage.length).toString(), JSON.stringify({ id: product.id, img: product.img, title: product.title, price: product.price, color: "black", size: "S", quantity: 1 }))
  }
}
