import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductsDataService } from 'src/app/services/products-data.service';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css'],
  providers: [ProductsDataService]
})
export class ProductDetailComponent implements OnInit {
  constructor(private productsDataService: ProductsDataService, private activatedRoute: ActivatedRoute, private toastr: ToastrService){}
  product!: {id: number, img: string, cat: string, title: string, price: number, desc: string, colors: Array<string>, sizes: Array<string>, bestseller: boolean} | undefined
  productId: any
  value: number = 1
  currentProduct: any
  currentSize: string = "XS"
  currentColor: string = "black"
  ngOnInit() {
    this.productId = this.activatedRoute.snapshot.paramMap.get('id')
    this.product = this.productsDataService.productsData.find(x => x.id == this.productId)
  }

  decreaseValue() {
    if(this.value>1) this.value--
  }

  increaseValue() {
    if(this.value<10) this.value++
  }

  getSize(size: HTMLSelectElement) {
    this.currentSize = size.value
  }
  getColor(color: HTMLInputElement) {
    this.currentColor = color.value.toLowerCase()
  }

  addToCart(product: any) {
    localStorage.setItem((localStorage.length).toString(), JSON.stringify({id: product.id, img: product.img, title: product.title, price: product.price * this.value, color: this.currentColor, size: this.currentSize, quantity: this.value}))
    this.toastr.success("Hello, I'm the toastr message.")

  }
}
