import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Wishlist } from 'src/app/models/wishlist.model';
import { CartService } from 'src/app/services/cart.service';
import { WishlistService } from 'src/app/services/wishlist.service';

@Component({
  selector: 'app-wishlist',
  templateUrl: './wishlist.component.html',
  styleUrls: ['./wishlist.component.css']
})
export class WishlistComponent implements OnInit {
  productsData: Wishlist[] = []
  constructor(private wishlistService: WishlistService ,private cartService: CartService, private router: Router, private productsServi) { }
  ngOnInit(): void {
    this.wishlistService.getWishlistData().subscribe(res => this.productsData = res)
    this.p
  }

  addToCart(product: Wishlist) {
    this.cartService.addToCart({ img: product.img, title: product.title, price: product.price, color: "black", size: "S", quantity: 1 }).subscribe(res => {
      if(res.status === 200) this.router.navigate(['/wishlist'])
    })
  }
}
