import { Component, OnInit } from '@angular/core';
import { CartService } from 'src/app/services/cart.service';

@Component({
  selector: 'app-wishlist',
  templateUrl: './wishlist.component.html',
  styleUrls: ['./wishlist.component.css']
})
export class WishlistComponent implements OnInit {
  productsData: Array<any> = []
  wishlistData!: any
  constructor(private cartService: CartService, private router) { }
  ngOnInit(): void {
    for(let i=0; i<sessionStorage.length; i++) {
      this.wishlistData = sessionStorage.getItem(i.toString())
      if(this.wishlistData != null) this.productsData.push(JSON.parse(this.wishlistData))
    }
  }

  addToCart(product: any) {
    this.cartService.addToCart({ img: product.img, title: product.title, price: product.price, color: "black", size: "S", quantity: 1 }).subscribe(res => {
      if(res.status === 200) this.router.navigate(['/wishlist'])
    })
  }
}
