  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  constructor(private productsDataService: ProductsDataService) {}
  productsData: Array<{id: number, img: string, cat: string, title: string, price: string, desc: string, colors: Array<string>, sizes: Array<string>}> = this.productsDataService.productsData
  ngOnInit(): void {

  }
}
