import { Component, Input, OnInit } from '@angular/core';
import { ProductsDataService } from 'src/app/services/products-data.service';
import { SearchDataService } from 'src/app/services/search-data.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit{
  constructor(private productsDataService: ProductsDataService, private searchDataService: SearchDataService) {}
  productsData: Array<{id: number, img: string, cat: string, title: string, price: string, desc: string, colors: Array<string>, sizes: Array<string>}> = this.productsDataService.productsData
  searchText: string = ""

  ngOnInit(): void {
    this.searchDataService.searchDataEmitter.subscribe((data:string) => this.searchText = data)
  }
}
