import { Component } from '@angular/core';

@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css']
})
export class CarouselComponent {
  slideIndex: number = 0
  sliderData: Array<{id: number, img: string, title: string, desc: string, desc2: string, bg: string}> = [
    {
        id: 1,
        img: "assets/cat5.jpg",
        title: "SUMMER SALE",
        desc: "Get your hands on latest trends!",
        desc2: "Avail 30% OFF", 
        bg: "f7d5cf"
    },
    {
        id: 2,
        img: "assets/slide3.png",
        title: "SUMMER SALE",
        desc: "Get your hands on latest trends!",
        desc2: "Avail 30% OFF",
        bg: "bbebe0"
    },
    {
        id: 3,
        img: "assets/slide1.jpeg",
        title: "SUMMER SALE",
        desc: "Get your hands on latest trends!",
        desc2: "Avail 30% OFF",
        bg: "f2e6d2"
    }
  ]

  handleClick(direction: string){
    if(direction === "left") {
        this.slideIndex > 0 ? this.slideIndex - 1 : 2
    }
    else{
        this.slideIndex < 2 ? this.slideIndex + 1 : 0
    }
  }
}
