import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductsDataService } from 'src/app/services/products-data.service';
import { SearchDataService } from 'src/app/services/search-data.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit{
  constructor(private productsDataService: ProductsDataService, private searchDataService: SearchDataService, private activatedRoute: ActivatedRoute) {}
  productsData!: Array<{id: number, img: string, cat: string, title: string, price: string, desc: string, colors: Array<string>, sizes: Array<string>}> 
  searchText: string = ""
  category!: string | null
  currentRoute!: string | null

  ngOnInit(): void {
    this.currentRoute = this.activatedRoute?.component?.name
    this.category = this.activatedRoute.snapshot.paramMap.get("cat")
    if(this.activatedRoute.snapshot.paramMap.get("cat")) this.productsData = this.productsDataService.productsData.filter(x => x.cat === this.category)
    else this.productsData = this.productsDataService.productsData
    this.searchDataService.searchDataEmitter.subscribe((data:string) => this.searchText = data)
  }
}
