import { Component } from '@angular/core';

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent {
  categoriesData: Array<{id: number, img: string, title: string, cat: string}> = [
    {
        id: 1,
        img: "images/cat1.png",
        title: "ETHNIC WEAR",
        cat: "ethnic"
    }, 
    {
        id: 2,
        img: "images/cat2.jpg",
        title: "WESTERN WEAR",
        cat: "western"
    },
    {
        id: 3,
        img: "images/cat3.png",
        title: "ACTIVE WEAR",
        cat: "active"
    },
    {
        id: 4,
        img: "images/cat4.png",
        title: "BAGS & HEELS",
        cat: "bags-heels"
    },
    {
        id: 5,
        img: "images/cat5.jpg",
        title: "GLASSES",
        cat: "glasses"
    },
    {
        id: 6,
        img: "images/cat6.png",
        title: "SKINCARE & BEAUTY",
        cat: "skincare-beauty"
    }
  ]
}
